import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Navbar from ".Components//Navbar";
import JoblyApi from "./api";
import CompanyList from './routes/CompanyList';
import JobList from "./routes/JobList";
import CompanyDetail from "./routes/CompanyDetail";
import LoginForm from './components/LoginForm';


function App() {
  const [companies, setCompanies] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [token, setToken] = useState(null);

  function login(token) {
    setToken(token);
    JoblyApi.token = token;
  };

  useEffect(() => {
    async function getCompanies() {
      let companies = await JoblyApi.getCompanies();
      setCompanies(companies);
    }
    getCompanies();
  }, []);

  useEffect(() => {
    async function getJobs() {
      let jobs = await JoblyApi.getJobs();
      setJobs(jobs);
    }
    getJobs();
  }, []);

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
  <Route exact path="/login" element={<LoginForm login={login} />} />
  <Route exact path="/companies" element={token ? <CompanyList companies={companies} /> : <Navigate to="/login" />} />
