import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Navbar from './Components/Navbar';
import JoblyApi from "./api";
import CompanyList from './routes/CompanyList';
import JobList from './routes/JobList';
import CompanyDetail from './routes/CompanyDetail';
import LoginForm from './Components/LoginForm';
import ProfileForm from "./Components/ProfileForm";
import HomePage from './Components/HomePage';
import SignupForm from "./Components/SignupForm";

function App() {
  const [companies, setCompanies] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [currentUser, setCurrentUser] = useState(null);

  // Update the login function to save the token to localStorage
  async function login(token) {
    setToken(token);
    localStorage.setItem("token", token);
    JoblyApi.token = token;

    try {
      const user = await JoblyApi.getCurrentUser();
      setCurrentUser(user);
    } catch (err) {
      console.error("Login error:", err);
    }
  }

  function logout() {
    setToken(null);
    setCurrentUser(null);
    JoblyApi.token = null;
    localStorage.removeItem("token");
  }

  async function handleSignup(signupData) {
    let token = await JoblyApi.signup(signupData);
    login(token);
  }

  // Logging token for debugging purposes
  useEffect(() => {
    console.log("Current Token:", token);
  }, [token]);

  useEffect(() => {
    async function getCompanies() {
      let companies = await JoblyApi.getCompanies();
      setCompanies(companies);
    }
    getCompanies();
  }, []);

  useEffect(() => {
    async function getJobs() {
      let jobs = await JoblyApi.getJobs();
      setJobs(jobs);
    }
    getJobs();
  }, []);

  return (
    <BrowserRouter>
      <Navbar currentUser={currentUser} logout={logout} />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/signup" element={<SignupForm signup={handleSignup} />} />
        <Route path="/login" element={<LoginForm login={login} />} />
        <Route path="/companies" element={token ? <CompanyList companies={companies} /> : <Navigate to="/login" />} />
        <Route path="/profile" element={<ProfileForm user={currentUser} updateUser={setCurrentUser} />} />
        <Route path="/jobs" element={<JobList jobs={jobs} />} />
        <Route path="/companies/:handle" element={<CompanyDetail />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
