import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Navbar from './Components/Navbar';
import JoblyApi from "./api";
import CompanyList from './routes/CompanyList';
import JobList from "./routes/JobList";
import CompanyDetail from "./routes/CompanyDetail";
import LoginForm from './Components/LoginForm';
import ProfileForm from "./Components/ProfileForm";
import HomePage from './Components/HomePage';



function App() {
  const [companies, setCompanies] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [token, setToken] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);


  function login(token) {
    setToken(token);
    JoblyApi.token = token;
  };

  useEffect(() => {
    async function getCompanies() {
      let companies = await JoblyApi.getCompanies();
      setCompanies(companies);
    }
    getCompanies();
  }, []);

  useEffect(() => {
    async function getJobs() {
      let jobs = await JoblyApi.getJobs();
      setJobs(jobs);
    }
    getJobs();
  }, []);

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
         <Route path="/" element={<HomePage />} />

        <Route path="/login" element={<LoginForm login={login} />} />
        <Route 
          path="/companies" 
          element={token ? <CompanyList companies={companies} /> : <Navigate to="/login" />} 
        />
        <Route
          path="/profile" 
          element={<ProfileForm user={currentUser} updateUser={setCurrentUser} />} 
        />
        <Route path="/jobs" element={<JobList jobs={jobs} />} />
        <Route path="/companies/:handle" element={<CompanyDetail />} />
      </Routes>
    </BrowserRouter>
  );

}

export default App;
